			addGMFunction(">Tknolg Base<",placeTknolgBase)
		else
			addGMFunction("Tknolg Base",placeTknolgBase)
		end
	else
		if gm_click_mode == "tknolg base" then
			gm_click_mode = nil
			onGMClick(nil)
		end
	end
